mapper.select:
select count(*)
from MEMBER
where EMAIL = #{email}

select count(*)
from MEMBER_DETAIL
where nickName = #{nickName}

SELECT m.MEMBER_ID AS memberId, MEMBER_ROLE_ID AS memberRoleId, EMAIL, NAME, TEL, POST_NUM AS postNum, ADDRESS1, ADDRESS2, NICKNAME, PASSWORD, REG_DATE AS regDate, PROFILE_IMG_NAME AS profileImgName, INFO FROM MEMBER m JOIN MEMBER_DETAIL md ON m.MEMBER_ID = md.MEMBER_ID LEFT JOIN ADDRESS_BUNDLE ab ON m.MEMBER_ID = ab.MEMBER_ID WHERE
m.MEMBER_ROLE_ID IN
#{roleId}
AND INSTR(md.NICKNAME, #{keyword}) > 0
AND INSTR(EMAIL, #{keyword}) > 0
ORDER BY REG_DATE DESC;
