mapper.select:
select ORDER_BUNDLE_ID orderBundleId,ob.MEMBER_ID memberId, ob.REG_DATE orderRegDate,SUM(QUANTITY) quantity,ob.buyer_name buyerName,ob.pay_method payMethod from ORDER_BUNDLE ob join MEMBER M on ob.MEMBER_ID = M.MEMBER_ID join ORDER_ITEM oi on ob.ORDER_BUNDLE_ID = oi.ORDER_ID join PRODUCT p on oi.PRODUCT_ID = p.PRODUCT_ID join ORDER_ITEM_STOCK_BUNDLE oisb on oi.ORDER_ITEM_ID = oisb.ORDER_ITEM_ID join (select ORDER_ITEM_ID,MAX(STATUS) status from ORDER_STATUS group by ORDER_ITEM_ID) t on t.ORDER_ITEM_ID = oi.ORDER_ITEM_ID
p.SELLER_ID = #{sellerId}
AND ob.REG_DATE BETWEEN #{startDate} AND #{endDate}
AND INSTR(ob.buyer_name, #{keyword}) > 0
AND INSTR(ob.pay_method, #{keyword}) > 0
group by ORDER_BUNDLE_ID,ob.MEMBER_ID,ob.REG_DATE,ob.buyer_name,ob.pay_method OFFSET #{offset} ROWS FETCH FIRST #{size} ROWS ONLY

SELECT COUNT(DISTINCT ob.ORDER_BUNDLE_ID) AS dataCount FROM ORDER_BUNDLE ob JOIN MEMBER M ON ob.MEMBER_ID = M.MEMBER_ID JOIN ORDER_ITEM oi ON ob.ORDER_BUNDLE_ID = oi.ORDER_ID JOIN PRODUCT p ON oi.PRODUCT_ID = p.PRODUCT_ID JOIN ORDER_STATUS os ON oi.ORDER_ITEM_ID = os.ORDER_ITEM_ID JOIN ORDER_ITEM_STOCK_BUNDLE oisb ON oi.ORDER_ITEM_ID = oisb.ORDER_ITEM_ID
p.SELLER_ID = #{sellerId}
AND ob.REG_DATE BETWEEN #{startDate} AND #{endDate}
AND INSTR(ob.buyer_name, #{keyword}) > 0
AND INSTR(ob.pay_method, #{keyword}) > 0
