mapper.update:
UPDATE product_img
SET sequence = sequence + 1
WHERE PRODUCT_ID = #{productId} and sequence >= #{sequence}

mapper.select:
SELECT COUNT(*)
FROM PRODUCT_SCRAP
WHERE MEMBER_ID = #{memberId} and PRODUCT_ID = #{productId}

SELECT option_id AS mainOptionId, parent_option_id AS parentOptionId, product_id AS productId, option_name AS optionName FROM product_main_option WHERE product_id = #{productId} AND parent_option_id
is null
= #{parentOptionId , jdbcType=VARCHAR}

SELECT option_detail_id AS optionDetailId, option_detail_name AS optionDetailName
FROM product_sub_option
WHERE option_id = #{mainOptionId}

SELECT option_price AS optionPrice, quantity, OPTION_DETAIL_ID AS optionDetailId1, option_detail_id2 AS optionDetailId2
FROM stock
WHERE option_detail_id = #{subOptionId}
